mapper.select:
select id, shop_name as shopName, picture as picture, shop_address as shopAddress, shop_description as shopDescription, shop_score as shopScore, thumb, collect, work_time as workTime, create_time as shopCreateTime, update_time as shopUpdateTime, is_deleted from shop where is_deleted = 0
And shop_position = #{position}
And floor = #{floor}
And shop_name LIKE CONCAT('%', #{searchValue}, '%')
order by create_time desc limit #{start},#{length};

select id, shop_name as shopName, picture as picture, shop_address as shopAddress, shop_description as shopDescription, shop_score as shopScore, thumb, collect, work_time as workTime, create_time as shopCreateTime, update_time as shopUpdateTime, is_deleted from shop where is_deleted = 0
And shop_position = #{position}
And floor = #{floor}
order by create_time desc

select id, shop_name as shopName, picture as picture, shop_address as shopAddress, shop_description as shopDescription, shop_score as shopScore, thumb, collect, work_time as workTime, create_time as shopCreateTime, update_time as shopUpdateTime, is_deleted from shop where 1=1
And (shop_name LIKE CONCAT('%', #{searchValue}, '%') or floor LIKE CONCAT('%', #{searchValue}, '%') or shop_description LIKE CONCAT('%', #{searchValue}, '%'))
order by create_time desc limit #{start},#{length};
